import React, { useRef, useState } from 'react';
import '../assets/styles/Contact.scss';
import emailjs from '@emailjs/browser';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import SendIcon from '@mui/icons-material/Send';
import TextField from '@mui/material/TextField';

function Contact() {

  const [name, setName] = useState<string>('');
  const [email, setEmail] = useState<string>('');
  const [message, setMessage] = useState<string>('');

  const [nameError, setNameError] = useState<boolean>(false);
  const [emailError, setEmailError] = useState<boolean>(false);
  const [messageError, setMessageError] = useState<boolean>(false);

  const form = useRef<HTMLFormElement>(null);

  const sendEmail = (e: React.FormEvent) => {
    e.preventDefault();

    setNameError(name === '');
    setEmailError(email === '');
    setMessageError(message === '');

    if (name !== '' && email !== '' && message !== '') {
      const templateParams = {
        name: name,
        email: email,
        message: message,
      };

      console.log(templateParams);
      emailjs.send('service_id', 'template_id', templateParams, 'api_key').then(
        (response) => {
          console.log('SUCCÈS!', response.status, response.text);
        },
        (error: Error) => {
          console.log('ÉCHEC...', error);
        }
      );
      setName('');
      setEmail('');
      setMessage('');
    }
  };

  return (
    <div id="contact">
      <div className="items-container">
        <div className="contact_wrapper">
          <h1>Contactez-Moi</h1>
          <p>Un projet à concrétiser ? Collaborons pour le réaliser !</p>
          <Box
            ref={form}
            component="form"
            noValidate
            autoComplete="off"
            className='contact-form'
          >
            <div className='form-flex'>
              <TextField
                required
                id="name-field"
                label="Votre Nom"
                placeholder="Quel est votre nom ?"
                value={name}
                onChange={(e) => {
                  setName(e.target.value);
                  if (nameError) setNameError(false);
                }}
                error={nameError}
                helperText={nameError ? "Veuillez entrer votre nom" : ""}
                InputLabelProps={{
                  shrink: true,
                }}
              />
              <TextField
                required
                id="email-field"
                label="Email / Téléphone"
                placeholder="Comment puis-je vous joindre ?"
                value={email}
                onChange={(e) => {
                  setEmail(e.target.value);
                  if (emailError) setEmailError(false);
                }}
                error={emailError}
                helperText={emailError ? "Veuillez entrer votre email ou numéro de téléphone" : ""}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </div>
            <TextField
              required
              id="message-field"
              label="Message"
              placeholder="Envoyez-moi vos demandes ou questions"
              multiline
              rows={10}
              className="body-form"
              value={message}
              onChange={(e) => {
                setMessage(e.target.value);
                if (messageError) setMessageError(false);
              }}
              error={messageError}
              helperText={messageError ? "Veuillez entrer votre message" : ""}
              InputLabelProps={{
                shrink: true,
              }}
            />
            <Button variant="contained" endIcon={<SendIcon />} onClick={sendEmail}>
              Envoyer
            </Button>
          </Box>
        </div>
      </div>
    </div>
  );
}

export default Contact;
